var fs = require('fs');
var lineReader = require('readline').createInterface({
  input: require('fs').createReadStream('emailsCKE.csv')
});

var allEmails =
		{
			
		};

lineReader.on('line', function (line) {
  //console.log('Line from file:', line);
  var entries = line.split(",");
  //console.log(entries);

  if (allEmails.hasOwnProperty(entries[0])) {
  	allEmails[entries[0]] += ", " + entries[1];
  } else {
  	allEmails[entries[0]] = entries[1];
  }

  //console.log(allPossibleChoices);

});

lineReader.on('close', function() {
	console.log(allEmails);
	fs.writeFile('emailsCKE2.json', JSON.stringify(allEmails, null, 4), function(err) {
	  if (err) throw err;
	  console.log('It\'s saved!');
	});
})


